<?phpini_set('display_errors', 1);require_once('../model/contact.php');require_once('../model/message.php');require_once('../control/valideToken.php');$user = getUserFromToken();if ($user !== false) {    $contact = $db->find('Contact', 'contact', 'id = :contact', array(':contact' => $parameters[':contact']));    if ($contact != null) {        $message = new Message();        $message->setContact($contact->getId());        $message->setMessage($parameters[':message']);        $message->setDate(date('Y-m-d H:i:s'));        $message->setUser($user->getId());        $message->setSent(true);        $id = $db->insert($message, 'message');        if ($id !== false) {            $message->setId($id);            $var = $message->toDB();            $json = array(                'error' => false,                'message' => $var            );        }    }}// echo json_encode($json, JSON_PRETTY_PRINT);            5.4 required!!echo json_encode($json);