<?phpini_set('display_errors', 1);require_once('./model/contact.php');require_once('./model/message.php');require_once('./control/valideToken.php');$user = getUserFromToken();if ($user !== false) {    $list = array();    $messages = $db->search('Message', 'message', 'contact = :contact', array(':contact' => $parameters[':contact']));    foreach ($messages as $message) {        if ($message->getUser() == $user->getId()) {            $message->setSent(true);        }        $list[] = $message->toDB();    }    $json = array(        'error' => false,        'messages' => $list    );}echo json_encode($json);